import React, { useEffect, useState } from 'react';

function UserList() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
  fetch('https://jsonplaceholder.typicode.com/users')
    .then((r) => r.json())
    .then((data) => {
      console.log('Fetched users:', data); // âœ… This line
      setUsers(data);
    });
}, []);


  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(search.toLowerCase()) ||
    user.email.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: '20px' }}>
      <h1>User Directory</h1>
      <input
        type="text"
        placeholder="Search users..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: '8px', marginBottom: '16px', width: '100%' }}
      />
      <ul>
        {filteredUsers.map((user) => (
          <li key={user.id} style={{ marginBottom: '15px' }}>
  <strong>{user.name}</strong><br />
  Email: {user.email}<br />
  Phone: {user.phone}<br />
  Company: {user.company.name}<br />
  Address: {user.address.street} ðŸ¤£ {user.address.suite} | {user.address.city} | {user.address.zipcode}
</li>
        ))}
      </ul>
    </div>
  );
}

export default UserList;
